import React, { Component } from 'react';
//import './App.css';
import General from './components/General'; 
//import SearchPage from '.components/SearchPage';
import {Container,Row,Col} from 'react-bootstrap';
import { Button } from 'react-bootstrap';
import { ButtonToolbar } from 'react-bootstrap';
import Navbar from 'react-bootstrap/Navbar'
import { Form } from 'react-bootstrap';
import { FormControl } from 'react-bootstrap';
import { Nav } from 'react-bootstrap';
import { NavDropdown } from 'react-bootstrap';
import Login from './components/Login';
import SearchPage from './components/SearchPage';
const axios = require('axios');
class App extends Component {
   constructor(props){
     super(props)
     this.state = {
       currentPage : <MainLogin main = {this}></MainLogin>,
         userName: '',
         userPassword: '',
         isUser: false,
         isUserAdmin: false

     }
    this.handleClick = this.handleClick.bind(this);
    this.handleChange = this.handleChange.bind(this);
   }
   handleChange(event){
    if(event.target.type === "text"){
      this.setState({userName: event.target.value});
    }
    if(event.target.type === "password"){
      this.setState({userPassword: event.target.value});
    }
   }
  handleClick(event){
    if(event.target.type === "submit"){
     this.checkUser();

    }
    if(event.target.name == "insert" && this.state.isUser === true){
      this.state.currentPage = <General/>;
      this.forceUpdate();
    }
    if(event.target.name == "search" && this.state.isUser === true){
      this.state.currentPage = <SearchPage />;
      this.forceUpdate();
    }
    if(event.target.name == "login" && this.state.isUserAdmin === true){
      this.state.currentPage = <Login />;
      this.forceUpdate();
    }
    else{
     this.state.currentPage = 
     <div>
       <Container>
      <Row>
    <Col >
     <h1>Not Authorized</h1>
    </Col>
  </Row>
    </Container>;
     </div>;
       this.forceUpdate();
  }


  }
  checkUser(){
    axios.post (`http://localhost:4000/users`,{
      headers: {"Content-Type": "application/json"},
      method: 'POST',
      data: {
        userName: this.state.userName,
        userPassword: this.state.userPassword
      }

    })
  .then(function (response) {
   if(response.data.length === 1){
     console.log("length 1");
   }
   console.log(response.data);
    return response.data;
  })
  .catch(function (error) {
    // handle error
    console.log("exist error")
    console.log(error);
  })
  .then(function () {


  });

  }
  checkAdmin(){
    axios.post (`http://localhost:4000/admins`,{
      headers: {"Content-Type": "application/json"},
      method: 'POST',
      data: {
        userName: this.state.userName,
        userPassword: this.state.userPassword
      }

    })
  .then(function (response) {
    return response.data;
  })
  .catch(function (error) {
    // handle error
    console.log("exist error")
    console.log(error);
  })
  .then(function () {


  });

  }

  render() {

    return (

      <div className="App" >
          <Container>
          <Navbar collapseOnSelect expand='lg' bg= 'light' variant= 'green' >
         <Navbar.Brand expand = "lg" bg = "gray" >{this.state.userName}</Navbar.Brand>
         <hr></hr>
          <Button onClick = {this.handleClick} variant="outline-primary" size = "lg" bg= "blue" name="insert">Insert A Report </Button>
          <hr></hr>
          <Button  onClick = {this.handleClick}  variant="outline-primary"  size = "lg" bg = "blue" name = "search">See Search Options</Button>
          <hr></hr>
          <Button onClick = {this.handleClick} variant="outline-primary" size = "lg" bg= "blue" name="login">Login</Button>
          <hr></hr>
        </Navbar>
       {this.state.currentPage}
          </Container>
      </div>
    );
  }
}

function MainLogin(props){
  return(
    <Container >
    <Form>
    <Form.Group controlId="formGroupUserName">
    <Form.Label>User Name: </Form.Label>
    <Form.Control type="text" placeholder="Enter User Name" onChange = {props.main.handleChange} />
   </Form.Group>
   <Form.Group controlId="formGroupPassword">
    <Form.Label>Password: </Form.Label>
    <Form.Control type="password" placeholder="Password" onChange = {props.main.handleChange}/>
    </Form.Group>
    <Button variant="primary" type="submit" onClick = {props.main.handleClick}>
    Log in
    </Button>
  </Form>
    </Container>

  );
}

export default App;
