import React, { Component } from 'react';
//import './App.css';
import logo from './netrdlogo.svg'
import General from './components/General'; 
//import SearchPage from '.components/SearchPage';
import {Container} from 'react-bootstrap';
import { Button } from 'react-bootstrap';
import { ButtonToolbar } from 'react-bootstrap';
import Navbar from 'react-bootstrap/Navbar'
import { Form , Col,Row} from 'react-bootstrap';
import { FormControl } from 'react-bootstrap';
import { Nav } from 'react-bootstrap';
import { NavDropdown } from 'react-bootstrap';
import Login from './components/Login';
import SearchPage from './components/SearchPage';
const axios = require('axios');
class App extends Component {
   constructor(props){
     super(props)
     this.state = {
       currentPage : <header/>,
       userName: '',
       userPassword: '',
       isUser: false,
       isUserAdmin: false
     }
    this.handleClick = this.handleClick.bind(this);
     this.handleChange = this.handleChange.bind(this);
     this.handleSubmit = this.handleSubmit.bind(this);
   }
   handleChange(event){
     console.log("handleChange entered");
    if(event.target.type === "text"){
      this.setState({userName: event.target.value});
    }
    if(event.target.type === "password"){
      this.setState({userPassword: event.target.value});
    }
   }
   handleSubmit(event){
     if(event.target.name = "sb"){
       console.log("submit entered");
        this.checkUser();
        this.checkAdmin();
     }
     else if(event.target.name = "sbo"){
       this.setState({isUser: false});
       this.setState({isUserAdmin: false});
     }

  }
   componentWillMount(){
     this.setState({ currentPage:
      <Container>
        <Col md={{ span: 6, offset: 3 }}>
      <Form>

    <Form.Group controlId="formBasicEmail">
    <Form.Label>User Name: </Form.Label>
    <Form.Control type="text" placeholder="Enter User Name" onChange = {this.handleChange}/>
    <Form.Text className="text-muted">
    </Form.Text>
  </Form.Group>

  <Form.Group controlId="formBasicPassword">
    <Form.Label>Password</Form.Label>
    <Form.Control type="password" placeholder="Password"  onChange = {this.handleChange}/>
  </Form.Group>

  </Form>

     <Row>
     <Button name= "sbo" onClick = {this.handleSubmit} disabled = {this.state.isUser || this.state.isUserAdmin}> 
        Log in
      </Button>
      <hr></hr>
      <Button name= "sbo" onClick = {this.handleSubmit} variant = "danger" disabled = { !this.state.isUser || !this.state.isUserAdmin}> 
        Log out
      </Button>
     </Row>
      </Col>
      </Container>
     });
   }

   checkUser(){
     let user = false;
    axios.post(`http://localhost:4000/users`,{
      headers: {"Content-Type": "application/json"},
      method: 'POST',
      data:{
        userName : this.state.userName,
        userPassword: this.state.userPassword
      }
    })
.then(function (response) {
  var arr = Object.values(response.data.data[0]);
  console.log(arr[0]);
  if(arr[0] === 1){
    user = true;
    }
})
.catch(function (error) {
  // handle error
  console.log(error);
})
.then(() => {
  this.setState({isUser: user});
});
   }
   checkAdmin(){
    let admin = false;
    axios.post(`http://localhost:4000/admins`,{
      headers: {"Content-Type": "application/json"},
      method: 'POST',
      data:{
        userName : this.state.userName,
        userPassword: this.state.userPassword
      }
    })
.then(function (response) {
  var arr = Object.values(response.data.data[0]);
  console.log(arr[0]);
  if(arr[0] === 1){
    admin = true;
    }
})
.catch(function (error) {
  // handle error
  console.log(error);
})
.then(() => {
  this.setState({isUserAdmin: admin});
});
   }
  handleClick(event){
    if(event.target.name == "insert" && this.state.isUser === true){
      this.setState({currentPage: <General/> })

    }
    else if(event.target.name == "search" && this.state.isUser === true){
      this.setState({currentPage: <SearchPage/> })
    }
    else if(event.target.name == "login" && this.state.isUserAdmin === true){
      this.setState({currentPage: <Login adminName = {this.state.userName}/> })
    }
    else if(event.target.name == "home"){
      this.setState({currentPage:
        <Container>
        <Col md={{ span: 6, offset: 3 }}>
      <Form>

    <Form.Group controlId="formBasicEmail">
    <Form.Label>User Name: </Form.Label>
    <Form.Control type="text" placeholder="Enter User Name" onChange = {this.handleChange}/>
    <Form.Text className="text-muted">

    </Form.Text>
  </Form.Group>

  <Form.Group controlId="formBasicPassword">
    <Form.Label>Password</Form.Label>
    <Form.Control type="password" placeholder="Password"  onChange = {this.handleChange}/>
  </Form.Group>

  </Form>

  <Row>
     <Button name= "sbo" onClick = {this.handleSubmit} disabled = {this.state.isUser || this.state.isUserAdmin}> 
        Log in
      </Button>
      <hr></hr>
      <Button name= "sbo" onClick = {this.handleSubmit} variant = "danger" disabled = { !this.state.isUser || !this.state.isUserAdmin}> 
        Log out
      </Button>
     </Row>
      </Col>
      </Container>
     });
    }
    else{
      this.setState({currentPage:
        <div>
          <h1> Not authorized </h1>
        </div>
      });
    }
    this.forceUpdate();
  }




  render() {

    return (

      <div className="App" >
          <Container>
          <Navbar collapseOnSelect expand='lg' bg= 'light' variant= 'green' >
          <img src={logo} height = "30"/>
         <Navbar.Brand expand = "lg" bg = "gray" >{this.state.currentUser} loggedin</Navbar.Brand>
         <hr></hr>
         <Button onClick = {this.handleClick} variant="outline-primary" size = "lg" bg= "blue" name="home">Home</Button>
          <hr></hr>
          <Button onClick = {this.handleClick} variant="outline-primary" size = "lg" bg= "blue" name="insert">Insert A Report </Button>
          <hr></hr>
          <Button  onClick = {this.handleClick}  variant="outline-primary"  size = "lg" bg = "blue" name = "search">See Search Options</Button>
          <hr></hr>
          <Button onClick = {this.handleClick} variant="outline-primary" size = "lg" bg= "blue" name="login">Control Panel</Button>
          <hr></hr>
        </Navbar>
       {this.state.currentPage}

          </Container>

      </div>
    );
  }
}



export default App;
