import React, { Component } from 'react';
//import './App.css';
import { Container } from 'react-bootstrap';
import { Nav } from 'react-bootstrap';
import { Form } from 'react-bootstrap';
import { FormControl } from 'react-bootstrap';
import { Navbar } from 'react-bootstrap';
import { Button } from 'react-bootstrap';

import BootstrapTable from 'react-bootstrap-table-next';
import filterFactory, { textFilter, dateFilter} from 'react-bootstrap-table2-filter';
import 'react-bootstrap-table2-filter/dist/react-bootstrap-table2-filter.min.css'
class SearchPage extends Component {
    constructor(props){
        super(props);
        this.state = {
            columns: [{
                dataField: 'generalinfo_id',
                text: 'Info ID',
                headerAlign: 'center',
                sort: true,
                filter: textFilter(),
                headerStyle:{ backgroundColor: '#81c784'}
              }, {
                dataField: 'fromcompletedby',
                text: 'Completed By',
                headerAlign: 'center',
                filter: textFilter(),
                headerStyle:{ backgroundColor: '#81c784'}
              }, {
                dataField: 'gpsproduct',
                text: 'Product',
                headerAlign: 'center',
                filter: textFilter(),
                headerStyle:{ backgroundColor: '#81c784'}
              },
              {
                dataField: 'startdate',
                text: 'Date',
                headerAlign: 'center',
                filter: dateFilter(),
                headerStyle:{ backgroundColor: '#81c784'}
              },
              {
                dataField: 'caseid',
                text: 'Case ID',
                sort: true,
                filter: textFilter(),
                headerFormatter: this.caseIDFormatter,
                headerAlign: 'center',
                headerStyle:{ backgroundColor: '#81c784'}
              },
              {
                dataField: 'customername',
                text: 'Customer Name',
                headerAlign: 'center',
                filter: textFilter(),
                headerStyle:{ backgroundColor: '#81c784'}
              },
              {
                dataField: 'description',
                text: 'Description',
                headerAlign: 'center',
                filter: textFilter(),
                headerStyle:{ backgroundColor: '#81c784'}
              },
              {
                dataField: 'pname',
                text: 'Problem',
                headerAlign: 'center',
                filter: textFilter(),
                headerStyle:{ backgroundColor: '#81c784'}
              }
                         
            ],
              generalinfo: [
                {
                  id: 1,
                  name: 'TV',
                  'price': 1000
                },
                {
                  id: 2,
                  name: 'Mobile',
                  'price': 500
                },
                {
                  id: 3,
                  name: 'Book',
                  'price': 20
                },
              ],

            
        }

    }
     caseIDFormatter(column, colIndex, { sortElement, filterElement }) {
        return (
          <div style={ { display: 'flex', flexDirection: 'column' } }>
            { filterElement }
            { column.text }
            { sortElement }
          </div>
        );
      }
   
    componentWillMount(){
      this.assignGeneralInfo();
      }


      assignGeneralInfo(){
        fetch('http://localhost:4000/generalinfo')
        .then(response => response.json())
        .then(({ data }) => {
          this.setState({generalinfo : data})
        
        })
        .catch(err => console.error()
        )
      }


    render() {

        return (

            <div className="App">
                <Container>
                    <Navbar bg="dark" variant="dark">
                        <Navbar.Brand href="#home">Navbar</Navbar.Brand>
                        <Nav className="mr-auto">
                        </Nav>
                        <Form inline>
                            <FormControl type="text" placeholder="Search" className="mr-sm-2" />
                            <Button variant="outline-info">Search</Button>
                        </Form>
                    </Navbar>
                    <BootstrapTable  filter={ filterFactory()}
   keyField='id' data = { this.state.generalinfo} columns = { this.state.columns }   striped
  hover
  condensed/>
                </Container>

            </div>
        );
    }
}


export default SearchPage;
