import React, { Component } from 'react';
import { Form, Row,Col ,Container,Button} from 'react-bootstrap';
import BootstrapTable from 'react-bootstrap-table-next';
import filterFactory, { textFilter, dateFilter} from 'react-bootstrap-table2-filter';

import paginationFactory from 'react-bootstrap-table2-paginator';
import 'react-bootstrap-table2-filter/dist/react-bootstrap-table2-filter.min.css'

class ViewUser extends Component {


constructor(props){
  super(props);
  this.state = {
    columns: [
        {
          dataField: 'adminname',
          text: 'Admin Name',
          headerAlign: 'center',
          sort: true,
          filter: textFilter(),
          headerStyle:{ backgroundColor: '#81c784',width:'150px'}
        },
        {
          dataField: 'username',
          text: 'User Name',
          headerAlign: 'center',
          sort: true,
          filter: textFilter(),
          headerStyle:{ backgroundColor: '#81c784',width:'150px'}
        },
        {
          dataField: 'userpassword',
          text: 'User Password',
          headerAlign: 'center',
          sort: true,
          filter: textFilter(),
          headerStyle:{ backgroundColor: '#81c784',width:'150px'}
        },
       ],
       users : [],
       selectedUsers: []
  }


}
componentWillMount(){
    this.assignData();
   }
   assignData(){

    fetch('http://localhost:4000/allusers')
    .then(response => response.json())
    .then(({ data }) => {
      console.log(data);
      this.setState({users : data})
      
    })
    .catch(err => console.error()
    )
   }

  render() {
    const selectRow = {
        mode: 'checkbox',
        clickToSelect: true,
        bgColor: (row, rowIndex) => (rowIndex % 2 == 0 ? '#00BFFF' : '#00FFFF'),
        clickToEdit: true,
        onSelect: (row, isSelect, rowIndex, e) => {
          if(isSelect === true){

           this.setState({selectedUsers: this.selectedUsers.push(row.username)});
          }
          if(isSelect === false){
            this.state.selectedUsers.splice(this.state.selectedUsers.indexOf(row.username),1);
          }
        
          
        }
    }
      return (
        <Container>
                 <Button    variant="danger" size="lg" active >
          Delete Selected Users
         </Button>
        <Row>
       <Col  md={{ span: 6, offset: 3}}>
       <BootstrapTable keyField = 'username' selectRow = {selectRow}  data = {this.state.users} columns = {this.state.columns}  
         pagination={ paginationFactory() }
           >


    </BootstrapTable>
    </Col>
    </Row>
    </Container>
      );
    }
    
    
  
}

export default ViewUser;
