import React from 'react';
import ProblemPage from './ProblemPage.js'
//import './styles/custom.css';
//import './styles/normalize.css';
//import './styles/prog-tracker.css';
import './styles/skeleton.css';
import { Button, DropdownButton } from 'react-bootstrap';
import { Navbar } from 'react-bootstrap';
import { Form } from 'react-bootstrap';
import { FormControl } from 'react-bootstrap';
import { Nav } from 'react-bootstrap';
import { Dropdown } from 'react-bootstrap';
import { Container } from 'react-bootstrap';
import { Col } from 'react-bootstrap';
import { Row } from 'react-bootstrap';
import DatePicker from "react-datepicker";
import 'react-datepicker/dist/react-datepicker.css';
import 'bootstrap/dist/css/bootstrap.min.css';
class General extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      date: new Date(),
      productTypes: [
        {name:'AS'},
        {name: 'SPiDR'},
        {name: 'KandyLink'},
        {name: 'GateWay'}
      ],
      currentProblem: '',
      problemTypes: [
        {
        name: 'Hardware'
      },
      {
        name: 'Upgrade'
      }
    ],
      problemPage: <hr></hr>,
      caseID: '',
      upgrade: '',
      from: '',
      toLoad: '',
      currentLoad: '',
      customerName: '',
      description: '',
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleClick = this.handleClick.bind(this);
    this.handleDate = this.handleDate.bind(this);
    this.handleDrop = this.handleDrop.bind(this);

  }

  handleChange(event) {
    this.setState({[[event.target.name]]: event.target.value});
  }
  handleDate(inputDate) {
    this.setState({ date: inputDate });
  }
  
  handleClick(e) {
    e.preventDefault();
    if(e.target.name === 'save'){
      alert("Report Saved!");
    }

    this.getInfo();

  }
  handleDrop(e){  
    e.preventDefault();
    if(e.target.value === 'Hardware'){
      this.setState({currentProblem: 'Hardware'});
      this.setState({problemPage: 
        <div>
          <hr></hr>
          <label>Current Load: </label>
                  <input name='currentLoad'
                    className='u-full-width'

                    type='text'
                    onChange={this.handleChange}
                    value={this.state.currentLoad}
                  />

        </div>
      })

    }
    else if(e.target.value === 'Upgrade'){
      this.setState({currentProblem: 'Upgrade'});
      this.setState({problemPage:
        <div>
        <hr></hr>
        <label>from: </label>
                <input name='from'
                  className='u-full-width'

                  type='text'
                  onChange={this.handleChange}
                  value={this.state.from}
                />
                 <label>to Load: </label>
                <input name='toLoad'
                  className='u-full-width'

                  type='text'
                  onChange={this.handleChange}
                  value={this.state.toLoad}
                />

      </div>
      })
    }
    else {
      this.setState({problemPage:
        <div>
        <hr></hr>
        <header></header>

      </div>
      })
    }
  }
  getInfo() {
    fetch('http://localhost:4000/generalinfo')
      .then(response => response.json())
      .then(({ data }) => {
        console.log(data)
      })
      .catch(err => console.error()
      )
  }
  addInfo() {
    fetch('http://localhost:4000/generalinfo/add')
      .then()
      .catch(err => console.error(err))
  }
  render() {
    return (
      <div >

        <Container>
          <Row>
            <Col>
              <header className='header' bg="blue" style= {{fontSize: 20}}>
                Please Select the date!</header>
              <DatePicker size = "lg"
              selected={this.state.date} onChange={this.handleDate}></DatePicker>
              <hr></hr>
              <div className='row'  >
                <div className='six columns'>
                  <label>Case ID: </label>
                  <input name='caseID'
                    className='u-full-width'

                    type='text'
                    onChange={this.handleChange}
                    value={this.state.caseID}
                  />
                </div>
              </div>
              
            <div className='row'>
              <div className='six columns'>
                <label>Name: </label>
                <input name='name'
                  className='u-full-width'

                  type='text'
                  onChange={this.handleChange}
                  value={this.state.name}
                />
              </div>
            </div>

            <div className='row'>
              <div className='six columns'>
                <label>Customer Name : </label>
                <input name='customerName'
                  className='u-full-width'

                  type='text'
                  onChange={this.handleChange}
                  value={this.state.customerName}
                />
              </div>
            </div>

            <div className='row'>
              <div className='six columns'>
                <label>  Description: </label>
                <textarea style={{ height: '100px', width: '300px' }} name='description' 
                  className='u-full-width'

                  type='textarea'
                  onChange={this.handleChange}
                  value={this.state.description}
                />
              </div>
            </div>

            </Col>
            <Col >
              <DropdownButton id="dropdown-item-button"  title = {this.state.currentProblem} size = "lg">
     

                  <Dropdown.Item as = "button" name = 'problemTypes' onClick= {this.handleDrop} 
                  value = 'Hardware'> Hardware</Dropdown.Item>
                  <Dropdown.Item as = "button" name = 'problemTypes'  onClick= {this.handleDrop}
                  value = 'Upgrade'> Upgrade </Dropdown.Item>
              </DropdownButton>
              {this.state.problemPage}
            </Col>
       
          </Row>
        </Container>
        <Container>
        <Button  onClick={this.handleClick} name='save' variant="danger" size="lg" active block>
        Save Report
        </Button>
        </Container>
        

      </div>
    )
  }
}
export default General;
