import React, { Component } from 'react';
//import './App.css';
import { Container } from 'react-bootstrap';
import { Nav } from 'react-bootstrap';
import { Form } from 'react-bootstrap';
import { FormControl } from 'react-bootstrap';
import { Navbar } from 'react-bootstrap';
import { Button } from 'react-bootstrap';

import BootstrapTable from 'react-bootstrap-table-next';
import filterFactory, { textFilter } from 'react-bootstrap-table2-filter';
class SearchPage extends Component {
    constructor(props){
        super(props);
        this.state = {
            columns: [{
                dataField: 'generalinfo_id',
                text: 'Info ID',
                sort: true
              }, {
                dataField: 'fromcompletedby',
                text: 'Completed By'
              }, {
                dataField: 'gpsproduct',
                text: 'Product'
              },
              {
                dataField: 'startdate',
                text: 'Date'
              },
              {
                dataField: 'caseid',
                text: 'Case ID',
                sort: true,
                filter: textFilter(),
                headerFormatter: caseIDFormatter
              },
              {
                dataField: 'customername',
                text: 'Customer Name'
              },
              {
                dataField: 'description',
                text: 'Description'
              },
              {
                dataField: 'pname',
                text: 'Problem'
              }
                         
            ],
              generalinfo: [
                {
                  id: 1,
                  name: 'TV',
                  'price': 1000
                },
                {
                  id: 2,
                  name: 'Mobile',
                  'price': 500
                },
                {
                  id: 3,
                  name: 'Book',
                  'price': 20
                },
              ],
            
        }

    }
   
    componentWillMount(){
      this.assignGeneralInfo();
      }


      assignGeneralInfo(){
        fetch('http://localhost:4000/generalinfo')
        .then(response => response.json())
        .then(({ data }) => {
          this.setState({generalinfo : data})
        
        })
        .catch(err => console.error()
        )
      }


    render() {

        return (

            <div className="App">
                <Container>
                    <Navbar bg="dark" variant="dark">
                        <Navbar.Brand href="#home">Navbar</Navbar.Brand>
                        <Nav className="mr-auto">
                        </Nav>
                        <Form inline>
                            <FormControl type="text" placeholder="Search" className="mr-sm-2" />
                            <Button variant="outline-info">Search</Button>
                        </Form>
                    </Navbar>
                    <BootstrapTable defaultSorted={ defaultSorted }   
   keyField='id' data = { this.state.generalinfo} columns = { this.state.columns }   striped
  hover
  condensed/>
                </Container>

            </div>
        );
    }
}
function caseIDFormatter(column, colIndex, { sortElement, filterElement }) {
    return (
      <div style={ { display: 'flex', flexDirection: 'column' } }>
        { filterElement }
        { column.text }
        { sortElement }
      </div>
    );
  }

export default SearchPage;
