import React, { Component } from 'react';
//import './App.css';
import { Container } from 'react-bootstrap';
import { Button } from 'react-bootstrap';
import { Row } from 'react-bootstrap';

import BootstrapTable from 'react-bootstrap-table-next';
import filterFactory, { textFilter, dateFilter} from 'react-bootstrap-table2-filter';

import paginationFactory from 'react-bootstrap-table2-paginator';
import 'react-bootstrap-table2-filter/dist/react-bootstrap-table2-filter.min.css'


import cellEditFactory, { Type } from 'react-bootstrap-table2-editor';
class SearchPage extends Component {
    constructor(props){
        super(props);
        this.state = {
           fieldcolumns:[
            {
              dataField:'pname',
              text:'Problem Name',
              headerAlign:'',
              headerStyle:{ backgroundColor: '#00ff00',width:'100px'},
              hidden: false
            },
             {
               dataField:'field1',
               text:'',
               headerAlign:'',
               headerStyle:{ backgroundColor: '#00ff00',width:'100px'},
               hidden: false
             },
             {
              dataField:'field2',
              text:'',
              headerAlign:'',
              headerStyle:{ backgroundColor: '#00ff00',width:'100px'},
              hidden: false
            },
            {
              dataField:'field3',
              text:'',
              headerAlign:'',
              headerStyle:{ backgroundColor: '#00ff00',width:'100px'},
              hidden: false
            },
            {
              dataField:'field4',
              text:'',
              headerAlign:'',
              headerStyle:{ backgroundColor: '#00ff00',width:'100px'},
              hidden: false
            },
            {
              dataField:'field5',
              text:'',
              headerAlign:'',
              headerStyle:{ backgroundColor: '#00ff00',width:'100px'},
              hidden: false
            },
            {
              dataField:'field6',
              text:'',
              headerAlign:'',
              headerStyle:{ backgroundColor: '#00ff00',width:'100px'},
              hidden: false
            },
             {
              dataField:'field7',
              text:'',
              headerAlign:'',
              headerStyle:{ backgroundColor: '#00ff00',width:'100px'},
              hidden: false
            },
            {
              dataField:'field8',
              text:'',
              headerAlign:'',
              headerStyle:{ backgroundColor: '#00ff00',width:'100px'},
              hidden: false
            },
            {
              dataField:'field9',
              text:'',
              headerAlign:'',
              headerStyle:{ backgroundColor: '#00ff00',width:'100px'},
              hidden: false
            }
           ],
           fielddata: [],
            columns: [{
                dataField: 'generalinfo_id',
                text: 'Info     ID',
                headerAlign: 'center',
                sort: true,
                filter: textFilter(),
                headerStyle:{ backgroundColor: '#81c784',width:'100px'}
              }, {
                dataField: 'fromcompletedby',
                text: 'Completed By',
                headerAlign: 'center',
                filter: textFilter(),
                headerStyle:{ backgroundColor: '#81c784',width:'100px'}
              }, {
                dataField: 'gpsproduct',
                text: 'Product',
                headerAlign: 'center',
                filter: textFilter(),
                editor: {
                  type: Type.SELECT,
                  options: [{

                    value: 'E',
                    label: 'E'
                  }]
                },
                headerStyle:{ backgroundColor: '#81c784',width:'100px'}
              },
              {
                dataField: 'startdate',
                text: 'Date',
                headerAlign: 'center',
                filter: dateFilter(),
                editable: false,
                editor:{
                  type : Type.DATE
                },
                headerStyle:{ backgroundColor: '#81c784',width:'100px'}
              },
              {
                dataField: 'caseid',
                text: 'Case        ID',
                sort: true,
                filter: textFilter(),
                headerFormatter: this.caseIDFormatter,
                headerAlign: 'center',
                headerStyle:{ backgroundColor: '#81c784',width:'100px'}
              },
              {
                dataField: 'customername',
                text: 'Customer Name',
                headerAlign: 'center',
                filter: textFilter(),
                headerStyle:{ backgroundColor: '#81c784',width:'100px'}
              },
              {
                dataField: 'description',
                text: 'Description',
                headerAlign: 'center',
                filter: textFilter(),
                editor: {
                  type: Type.TEXTAREA
                },
                headerStyle:{ backgroundColor: '#81c784',width:'250px'}
              },
              {
                dataField: 'pname',
                text: 'Problem     Name',
                headerAlign: 'center',
                filter: textFilter(),
                editor: {
                  type: Type.SELECT,
                  options: [{

                    value: 'E',
                    label: 'E'
                  }]
                },
                headerStyle:{ backgroundColor: '#81c784',width:'100px'}
              }

                         
            ],
          
              selectedRows: [],

              generalinfo: [
               
              ],

            
        }
        this.handleSelected = this.handleSelected.bind(this);
        this.saveChanges = this.saveChanges.bind(this);
        this.deleteRows = this.deleteRows.bind(this);
    }

 
     caseIDFormatter(column, colIndex, { sortElement, filterElement }) {
        return (
          <div style={ { display: 'flex', flexDirection: 'column' } }>
            { filterElement }
            { column.text }
            { sortElement }
          </div>
        );
      }
      handleSelected(){
          console.log(this.state.generalinfo.dataField);
      }
     
    componentWillMount(){
      this.assignGeneralInfo();
      this.assingProductBox();
      this.assignProblemBox();
      }

    deleteRows(){
      let l = this.state.selectedRows.length;
      if (window.confirm("Are you sure to delete " + l+ " reports ?")) {
        for(let row of this.state.selectedRows){
          this.deleteRow(row);
        }
        this.assignGeneralInfo();
        this.forceUpdate();
      } else {
        this.setState({selectedRows : []});
      }
  
      
    }
    deleteRow(generalinfo_id){
      const axios = require('axios');
      axios.post(`http://localhost:4000/generalinfo/remove?generalinfo_id=${generalinfo_id}`,{
        headers: {"Content-Type": "text/plain"},
        method: 'POST',
        data: "delete sent"
      })
    .then(function (response) {
      // handle success
      console.log(response.data);
    })
    .catch(function (error) {
      // handle error
      console.log(error);
    })
    .then(function () {

      
    });
      alert("Deleted");

    }
    saveChanges(generalinfo_id,fromcompletedby,gpsproduct,startdate,caseid,customername,description,pname){
      const axios = require('axios');
      axios.post(`http://localhost:4000/generalinfo/update?generalinfo_id=${generalinfo_id}&startdate=${startdate}&caseid=${caseid}
      &gpsproduct=${gpsproduct}&fromcompletedby=${fromcompletedby}&customername=${customername}&pname=${pname}`,{
