import React, { Component } from 'react';
//import './App.css';
import { Container } from 'react-bootstrap';
import { Nav } from 'react-bootstrap';
import { Form } from 'react-bootstrap';
import { FormControl } from 'react-bootstrap';
import { Navbar } from 'react-bootstrap';
import { Button } from 'react-bootstrap';
import { Row } from 'react-bootstrap';

import BootstrapTable from 'react-bootstrap-table-next';
import filterFactory, { textFilter, dateFilter} from 'react-bootstrap-table2-filter';

import paginationFactory from 'react-bootstrap-table2-paginator';
import 'react-bootstrap-table2-filter/dist/react-bootstrap-table2-filter.min.css'

import cellEditFactory, { Type } from 'react-bootstrap-table2-editor';
class SearchPage extends Component {
    constructor(props){
        super(props);
        this.state = {
            columns: [{
                dataField: 'generalinfo_id',
                text: 'Info     ID',
                headerAlign: 'center',
                sort: true,
                filter: textFilter(),
                headerStyle:{ backgroundColor: '#81c784'}
              }, {
                dataField: 'fromcompletedby',
                text: 'Completed By',
                headerAlign: 'center',
                filter: textFilter(),
                headerStyle:{ backgroundColor: '#81c784'}
              }, {
                dataField: 'gpsproduct',
                text: 'Product',
                headerAlign: 'center',
                filter: textFilter(),
                headerStyle:{ backgroundColor: '#81c784'}
              },
              {
                dataField: 'startdate',
                text: 'Date',
                headerAlign: 'center',
                filter: dateFilter(),
                editable: false,
                headerStyle:{ backgroundColor: '#81c784'}
              },
              {
                dataField: 'caseid',
                text: 'Case        ID',
                sort: true,
                filter: textFilter(),
                headerFormatter: this.caseIDFormatter,
                headerAlign: 'center',
                headerStyle:{ backgroundColor: '#81c784'}
              },
              {
                dataField: 'customername',
                text: 'Customer Name',
                headerAlign: 'center',
                filter: textFilter(),
                headerStyle:{ backgroundColor: '#81c784'}
              },
              {
                dataField: 'description',
                text: 'Description',
                headerAlign: 'center',
                filter: textFilter(),
                editor: {
                  type: Type.TEXTAREA
                },
                headerStyle:{ backgroundColor: '#81c784'}
              },
              {
                dataField: 'pname',
                text: 'Problem     Name',
                headerAlign: 'center',
                filter: textFilter(),
                headerStyle:{ backgroundColor: '#81c784'}
              }

                         
            ],
              selecteds: [],
              expandRow : {
                showExpandColumn: true,
                renderer: row => (
                  <div>
                    <p>{ `This Expand row is belong to rowKey ${row.dataField}` }</p>
                    <p>You can render anything here, also you can add additional data on every row object</p>
                    <p>expandRow.renderer callback will pass the origin row object to you</p>
                  </div>
                )
              },
              generalinfo: [
               
              ],

            
        }
        this.handleSelected = this.handleSelected.bind(this);
        this.saveChanges = this.saveChanges.bind(this);
        this.deleteRows = this.deleteRows.bind(this);
    }
     caseIDFormatter(column, colIndex, { sortElement, filterElement }) {
        return (
          <div style={ { display: 'flex', flexDirection: 'column' } }>
            { filterElement }
            { column.text }
            { sortElement }
          </div>
        );
      }
      handleSelected(){
          console.log(this.state.generalinfo.dataField);
      }
   
    componentWillMount(){
      this.assignGeneralInfo();
      }
    componentDidMount(){
      console.log(this.state.generalinfo);
    }
    deleteRows(){

      alert("Deleted");

    }
    saveChanges(){
      alert("changes saved");
    }
      assignGeneralInfo(){
        fetch('http://localhost:4000/generalinfo')
        .then(response => response.json())
        .then(({ data }) => {
          this.setState({generalinfo : data})
          
        })
        .catch(err => console.error()
        )
      }


    render() {
      const selectRow = {
        mode: 'checkbox',
        clickToSelect: true,
        bgColor: '#00BFFF',
        clickToExpand: true,
        clickToEdit: true
      };
      const cellEdit = {
        mode: 'click'
      };
        return (

            <div className="Appp">
                <Container>
                  <Row>
                  <Button  onClick={this.deleteRows} input ="newProduct" variant="danger" size="lg" active >
          Delete Selected Rows
         </Button>
         <hr></hr>
         <Button  onClick={this.saveChanges} input ="newProduct" variant="danger" size="lg" active >
         Save Changes
         </Button>

                  </Row>
                  <hr></hr>
                  <Row>
                  <BootstrapTable  filter={ filterFactory()} selectRow={ selectRow }  
                  cellEdit={ cellEditFactory({ mode: 'dbclick',
                  afterSaveCell: (oldValue, newValue, row, column) => { alert('After Saving Cell!!'); }
                }) } 
                  pagination={ paginationFactory() }
                      keyField='generalinfo_id' data = { this.state.generalinfo} columns = { this.state.columns }  
                      striped hover condensed
  
  />
                    
                  </Row>
  
                </Container>

            </div>
        );
    }
}


export default SearchPage;
