'use strict'
import React from 'react';
import mysql from 'mysql';
//import './styles/custom.css';
//import './styles/normalize.css';
//import './styles/prog-tracker.css';
import './styles/skeleton.css';
import { Button, DropdownButton } from 'react-bootstrap';
import { Navbar } from 'react-bootstrap';
import { Form } from 'react-bootstrap';
import { FormControl } from 'react-bootstrap';
import { Nav } from 'react-bootstrap';
import { Dropdown } from 'react-bootstrap';
import { Container } from 'react-bootstrap';
import { Col } from 'react-bootstrap';
import { Row } from 'react-bootstrap';
import DatePicker from "react-datepicker";
import 'react-datepicker/dist/react-datepicker.css';
import 'bootstrap/dist/css/bootstrap.min.css';
class General extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      date: new Date(),
      problemType: 'Choose Problem Type!',
      caseID: '',
      upgrade: '',
      from: '',
      toLoad: '',
      currentLoad: '',
      customerName: '',
      description: ''
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleClick = this.handleClick.bind(this);
    this.handleDate = this.handleDate.bind(this);

  }

  handleChange(event) {
    this.setState({ [event.target.name]: event.target.value });
  }
  handleDate(inputDate) {
    this.setState({ date: inputDate });
  }
  handleClick(e) {
    e.preventDefault();
    console.log('clicked');

    this.getInfo();


  }
  getInfo() {
    fetch('http://localhost:4000/generalinfo')
      .then(response => response.json())
      .then(({ data }) => {
        console.log(data)
      })
      .catch(err => console.error()
      )
  }
  addInfo() {
    fetch('http://localhost:4000/generalinfo/add')
      .then()
      .catch(err => console.error(err))
  }
  render() {
    return (
      <div>

        <Container>
          <Row>
            <Col>
              <header className='header' bg="blue">
                SELECT THE DATE</header>
              <DatePicker 
              strictParsing
              selected={this.state.date} onChange={this.handleDate}></DatePicker>

              <div className='row'>
                <div className='six columns'>
                  <label>caseID: </label>
                  <input name='caseID'
                    className='u-full-width'

                    type='text'
                    onChange={this.handleChange}
                    value={this.state.caseID}
                  />
                </div>
              </div>
            </Col>
            <Col>
              <DropdownButton id="dropdown-item-button"  title = {this.state.problemType} size = "lg">
     

                  <Dropdown.Item as = "button" name = 'problemType' onClick= {this.handleClick} 
                  value = 'Hardware'> Hardware</Dropdown.Item>
                  <Dropdown.Item as = "button" name = 'problemType'  onClick= {this.handleClick}
                  value = 'Upgrade'> Upgrade</Dropdown.Item>
              </DropdownButton>
            </Col>


          </Row>
          <Row>

            <div className='row'>
              <div className='six columns'>
                <label>Name: </label>
                <input name='name'
                  className='u-full-width'

                  type='text'
                  onChange={this.handleChange}
                  value={this.state.name}
                />
              </div>
            </div>

            <div className='row'>
              <div className='six columns'>
                <label>Customer Name : </label>
                <input name='customerName'
                  className='u-full-width'

                  type='text'
                  onChange={this.handleChange}
                  value={this.state.customerName}
                />
              </div>
            </div>

            <div className='row'>
              <div className='six columns'>
                <label>  Description: </label>
                <textarea style={{ height: '100px', width: '300px' }} name='description'
                  className='u-full-width'

                  type='textarea'
                  onChange={this.handleChange}
                  value={this.state.description}
                />
              </div>
            </div>
          </Row>
        </Container>










        <button onClick={this.handleClick} name='save'> Save Report</button>

      </div>
    )
  }
}
export default General;
