      clickToExpand: true,
      clickToEdit: true,
      onSelect: (row, isSelect, rowIndex, e) => {

        if (isSelect === true) {
          this.state.selectedRows.push(row);

       
          this.setFieldContents(row.caseid);
        }
        if (isSelect === false) {
          this.state.selectedRows.splice(this.state.selectedRows.indexOf(row), 1);
       
        }


      },
      onSelectAll: (isSelect, rows, e) => {
        if (isSelect === true) {
