import React, { Component } from 'react';
import { Form, Row,Col ,Container,Button} from 'react-bootstrap';
import BootstrapTable from 'react-bootstrap-table-next';
import filterFactory, { textFilter, dateFilter} from 'react-bootstrap-table2-filter';

import paginationFactory from 'react-bootstrap-table2-paginator';
import 'react-bootstrap-table2-filter/dist/react-bootstrap-table2-filter.min.css'
const axios = require('axios');
class ViewUser extends Component {


constructor(props){
  super(props);
  this.state = {
    columns: [
        {
          dataField: 'adminname',
          text: 'Admin Name',
          headerAlign: 'center',
          sort: true,
          filter: textFilter(),
          headerStyle:{ backgroundColor: '#81c784',width:'150px'}
        },
        {
          dataField: 'username',
          text: 'User Name',
          headerAlign: 'center',
          sort: true,
          filter: textFilter(),
          headerStyle:{ backgroundColor: '#81c784',width:'150px'}
        },
        {
          dataField: 'userpassword',
          text: 'User Password',
          headerAlign: 'center',
          sort: true,
          filter: textFilter(),
          headerStyle:{ backgroundColor: '#81c784',width:'150px'}
        },
       ],
       users : [],
       selectedUsers: []
  }


}
assignData(){

  fetch('http://localhost:4000/allusers')
  .then(response => response.json())
  .then(({ data }) => {
    console.log(data);
    this.setState({users : data})
    
  })
  .catch(err => console.error()
  )
 }
deleteUsers(){
  var copySelected = this.state.selectedUsers;
    // for(let user of copySelected){
    //     this.deleteUser(user);
    // }
 console.log(copySelected[0]);
}
deleteUser(){
    let us = this.state.selectedUsers[0];
    axios.post(`http://localhost:4000/users/remove`,{
      headers: {"Content-Type": "application/json"},
      method: 'POST',
      data: {
          userName: us
      }
    })
  .then(function (response) {
    // handle success
    console.log(response.data);
  })
  .catch(function (error) {
    // handle error
    console.log(error);
  })
  .then(() => {
    this.assignData();
  
  });
    alert("Deleted");
}
componentDidMount(){
    this.assignData();
   }


  render() {
    const selectRow = {
        mode: 'checkbox',
        hideSelectAll: true,
        clickToSelect: true,
        bgColor: (row, rowIndex) => (rowIndex % 2 == 0 ? '#00BFFF' : '#00FFFF'),
        clickToEdit: true,
        onSelect: (row, isSelect, rowIndex, e) => {
            if(isSelect === true){
               let copiedUsers = this.state.selectedUsers;
               copiedUsers.push(row.username);
               this.setState({selectedUsers: copiedUsers});
              }
              if(isSelect === false){
                let copiedUsers = this.state.selectedUsers;
                copiedUsers.splice(this.state.selectedUsers.indexOf(row.username),1);
                this.setState({selectedUsers: copiedUsers});
              }
             console.log(row.rowKey);
          
        }
    }
      return (
        <Container >
            <Row>
                <Col  md={{ span: 6, offset: 5}}>
                <Button    variant="danger" size="lg" active onClick = {this.deleteUser} >
                        Delete Selected Users
                    </Button>
                </Col>
            </Row>
        
        <Row>
       <Col  md={{ span: 6, offset: 3}}>
       <BootstrapTable keyField = 'username' selectRow = {selectRow}  data = {this.state.users} columns = {this.state.columns}  
         pagination={ paginationFactory() }
           >


    </BootstrapTable>
    </Col>
    </Row>
    </Container>
      );
    }
    
    
  
}

export default ViewUser;
